mapper.select:
SELECT EMP_ID
  FROM EMPLOYEE
 WHERE EMP_NAME=#{empName}
 	   AND EMP_PRI_EMAIL=#{empPriEmail}
 	   AND RESIGN_FLAG='N'

SELECT *
  FROM EMPLOYEE
 WHERE EMP_ID=#{empId}
 	   AND EMP_PRI_EMAIL=#{empPriEmail}
 	   AND RESIGN_FLAG='N'

mapper.update:
UPDATE EMPLOYEE
   SET EMP_PWD=#{empPwd}
 WHERE EMP_ID=#{empId}
 	   AND EMP_PRI_EMAIL=#{empPriEmail}
 	   AND RESIGN_FLAG='N'

mapper.select:
SELECT COUNT(*)
  FROM EMPLOYEE
 WHERE EMP_ID=#{empId}
